    private void b1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_b1ActionPerformed
        try{
            
            String a = t1.getText();
            String b = p1.getText();
            
            Class.forName("com.mysql.jdbc.Driver");
            Connection con = DriverManager.getConnection("jdbc:mysql://localhost:3306/pwdgen?zeroDateTimeBehavior=convertToNull", "root", "");
            Statement stmt = con.createStatement();
            
            ResultSet u = stmt.executeQuery("select * from users where uName = '" + a + "' and pwd = '" + b + "' ");
            
            if(u.next()){
                this.setVisible(false);
                PasswordGenerator pg = new PasswordGenerator(u.getInt(1) ,u.getString(2));
                pg.setVisible(true);
            }
            else
                JOptionPane.showMessageDialog(null, "Username or password do not match.\nPlease try again");
        }
        catch(Exception e){
        }
        
        
        
    }//GEN-LAST:event_b1ActionPerformed

